import React, { useEffect, useState } from 'react';
import './autores.css';


const AutoresPage = () => {
  const [autorData, setAutorData] = useState([]);
  const [isCreating, setIsCreating] = useState(false);
  const [newAutorData, setNewAutorData] = useState({
    nome: '',
    sobrenome: '',
    nacionalidade: '',
  });

  async function fetchAutores() {
    const response = await fetch(`http://localhost:8080/autor`);
    const data = await response.json();
    return data;
  }

  async function createAutor(authorData) {
    try {
      const response = await fetch('http://localhost:8080/autor', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(authorData),
      });

      if (!response.ok) {
        throw new Error('Failed to create author');
      }

      // Author created successfully, fetch the updated list of authors
      const updatedAutores = await fetchAutores();
      setAutorData(updatedAutores);
      setIsCreating(false);
      setNewAutorData({
        nome: '',
        sobrenome: '',
        nacionalidade: '',
      });
    } catch (error) {
      console.error(error);
      // Handle the error here, show an error message, or perform any necessary actions
    }
  }

  useEffect(() => {
    fetchAutores().then(data => {
      setAutorData(data);
      console.log(data);
    });
  }, []);

  return (
    <div>
      <h2>PÃ¡gina de Autores</h2>
      {autorData.length === 0 ? (
        <div>
          <p>Carregando os dados...</p>
        </div>
      ) : (
        <div className="table-container">
          <h3>Os dados foram carregados com sucesso!</h3>
          <button onClick={() => setIsCreating(true)}>Create Autor</button>
          {isCreating && (
            <div>
              <h3>Create New Autor</h3>
              <form onSubmit={(e) => {
                e.preventDefault();
                createAutor(newAutorData);
              }}>
                <label>
                  ID:
                  <input
                    type="text"
                    value={newAutorData.id}
                    onChange={(e) =>
                      setNewAutorData({ ...newAutorData, id: e.target.value })
                    }
                  />
                </label>
                <label>
                  Nome:
                  <input
                    type="text"
                    value={newAutorData.nome}
                    onChange={(e) =>
                      setNewAutorData({ ...newAutorData, nome: e.target.value })
                    }
                  />
                </label>
                <label>
                  Sobrenome:
                  <input
                    type="text"
                    value={newAutorData.sobrenome}
                    onChange={(e) =>
                      setNewAutorData({ ...newAutorData, sobrenome: e.target.value })
                    }
                  />
                </label>
                <label>
                  Nacionalidade:
                  <input
                    type="text"
                    value={newAutorData.nacionalidade}
                    onChange={(e) =>
                      setNewAutorData({ ...newAutorData, nacionalidade: e.target.value })
                    }
                  />
                </label>
                <button type="submit">Create</button>
                <button onClick={() => setIsCreating(false)}>Cancel</button>
              </form>
            </div>
          )}
          <table>
            <thead>
              <tr>
                <th>Id</th>
                <th>Nome</th>
                <th>Sobrenome</th>
                <th>Nacionalidade</th>
              </tr>
            </thead>
            <tbody>
              {autorData.map(autor => (
                <tr key={autor.id}>
                  <td>{autor.id}</td>
                  <td>{autor.nome}</td>
                  <td>{autor.sobrenome}</td>
                  <td>{autor.nacionalidade}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

const AutorPageId = ({ id }) => {
  const [autorData, setAutorData] = useState([]);

  useEffect(() => {
    const fetchAutor = async () => {
      const response = await fetch(`http://localhost:8080/autor/${id}`);
      const data = await response.json();
      setAutorData(data);
      console.log(data);
    };

    fetchAutor();
  }, [id]);

  return (
    <div>
      {autorData.length === 0 ? (
        <div>
          <p>..</p>
        </div>
      ) : (
        <div>
          <div key={autorData.id}>
          <p>{autorData.nome} {autorData.sobrenome}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export { AutoresPage, AutorPageId };
